javascript:
/*Pop Up
$('section[data-section-idx="5"]').attr('style', 'padding-top: 100px !important; padding-bottom: 100px !important; border-radius: var(--Default-space, 0px);');
*/
/**/
let record = {
    "doctype":"Pop Up TechMax",
    "web_form_name":"pop-up-tmx"
};

$('.input, .form-select').on('change', ev => {
    let el = $(event.target);
    record[el.attr('name')] = el.val();
    console.log(record);
});


$('.btn-primary').on('click', ev => {

    ev.preventDefault();

    if (!Object.keys(record).length === 6) {
        frappe.throw('Todos os campos são obrigatórios');
    }

    frappe.call({
        'method': 'frappe.website.doctype.web_form.web_form.accept',
        'args': {
            'data': record,
            'for_payment': false,
            'web_form': 'pop-up-tmx'

        },
        'callback': res => {
            frappe.msgprint({
                title: "TechMax",
                message: 'Obrigado pelo seu contato, retornaremos em breve!'
            });
        }
    });
});

