/*prev*/
function customizeSwiperPrevButton() {
  // Selecione o elemento do botão prev
  var prevButton = document.querySelector('.swiper-button-prev');

  // Verifique se o elemento existe antes de prosseguir
  if (prevButton) {
    // Crie um elemento SVG
    var svgElement = document.createElementNS("http://www.w3.org/2000/svg", "svg");
    svgElement.setAttribute("xmlns", "http://www.w3.org/2000/svg");
    svgElement.setAttribute("width", "25");
    svgElement.setAttribute("height", "24");
    svgElement.setAttribute("viewBox", "0 0 25 24");
    svgElement.setAttribute("fill", "none");

    // Adicione os caminhos ao elemento SVG
    svgElement.innerHTML = `
      <path d="M5.5 12H19.5" stroke="#38424E" stroke-width="1.75" stroke-linecap="round" stroke-linejoin="round"/>
      <path d="M5.5 12L11.5 18" stroke="#38424E" stroke-width="1.75" stroke-linecap="round" stroke-linejoin="round"/>
      <path d="M5.5 12L11.5 6" stroke="#38424E" stroke-width="1.75" stroke-linecap="round" stroke-linejoin="round"/>
    `;

    // Limpe o conteúdo do botão prev e adicione o novo SVG
    prevButton.innerHTML = '';
    prevButton.appendChild(svgElement);
  }
}

// Chame a função para personalizar o botão prev
customizeSwiperPrevButton();

/*next*/
function customizeSwiperNextButton() {
  // Selecione o elemento do botão next
  var nextButton = document.querySelector('.swiper-button-next');

  // Verifique se o elemento existe antes de prosseguir
  if (nextButton) {
    // Crie um elemento SVG
    var svgElement = document.createElementNS("http://www.w3.org/2000/svg", "svg");
    svgElement.setAttribute("xmlns", "http://www.w3.org/2000/svg");
    svgElement.setAttribute("width", "25");
    svgElement.setAttribute("height", "24");
    svgElement.setAttribute("viewBox", "0 0 25 24");
    svgElement.setAttribute("fill", "none");

    // Adicione os caminhos ao elemento SVG
    svgElement.innerHTML = `
      <path d="M19.5 12H5.5" stroke="#38424E" stroke-width="1.75" stroke-linecap="round" stroke-linejoin="round"/>
      <path d="M19.5 12L13.5 18" stroke="#38424E" stroke-width="1.75" stroke-linecap="round" stroke-linejoin="round"/>
      <path d="M19.5 12L13.5 6" stroke="#38424E" stroke-width="1.75" stroke-linecap="round" stroke-linejoin="round"/>
    `;

    // Limpe o conteúdo do botão next e adicione o novo SVG
