    color: rgba(96, 105, 123, 1) !important;
}
.about-2-cards .card-body{
    padding: 40px 44px;
    display: flex;
    align-items: center;
    flex-direction: column;
    justify-content: center;
}

/*Testemunho*/

.wrapper-testimonials-4tech .card1{
    width: 100% !important;
}
.wrapper-testimonials-4tech .w-auto{
    z-index: 5;
}

