    display: none;
}

.wrapper-testimonials-4tech .swiper-pagination-bullets.swiper-pagination-horizontal{
    width: 105% !important;
}


.wrapper-testimonials-4tech{
    background-color: #fff !important;
}

.wrapper-testimonials-4tech .swiper-content .swiper-controls{
    display: none !important;
}
.wrapper-testimonials-4tech .row{
    background: rgba(33, 103, 120, 0.08) !important;
}

.wrapper-testimonials-4tech .swiper-backface-hidden{
    padding: 120px 50px;
    align-items: center;
    gap: 0px;
    flex: 1 0 0;
    border-radius: 8px;
    height: 535px !important;
    z-index: 10 !important;
}


/*Perguntas*/
.faq-1 .subtitle {
    margin-top: 24px;
    margin-bottom: 34px !important;
	color: rgba(101, 109, 120, 1) !important;
	font-family: 'Work Sans', sans-serif !important;
	font-size: 18px !important;
	font-style: normal;
	font-weight: 500 !important;
	line-height: 24px !important;
}
.faq-1 .accordion-button {
    font-size: 18px !important;
    font-weight: 500;
    font-family: "Work Sans", sans-serif;
    line-height: 24px;
}
.faq-1 [data-bs-toggle="collapse"]:not(.collapsed) {
    color: rgba(33, 103, 120, 1) !important;
}
.faq-1 .accordion-collapse p {
    font-family: "Manrope", sans-serif;
    font-size: 15px;
    font-weight: 500;
    line-height: 22px;
    text-align: justify;
}
#chevron-icon{
    position: relative;
    left: -10px !important;
}

.rotate {
    transform: rotate(0deg);
    transition: transform 0.3s ease-in-out; 
}

.accordion-button[aria-expanded="false"] .rotate {
    transform: rotate(0deg) !important; 
}

.accordion-button[aria-expanded="true"] .rotate {
    transform: rotate(-180deg); 
}
.wrapper-testimonials-4tech .subtitle-section{
    color: #656D78;
    font-size: 18px;
    font-weight: 500;
    font-family: 'Work Sans', sans-serif;
    line-height: 24px;
}
.wrapper-testimonials-4tech .swiper-controls .swiper-pagination .swiper-pagination-bullet.swiper-pagination-bullet-active{
    width: 34px !important;
