}
.wrapper-testimonials-4tech .swiper-pagination-bullet{
    width: 34px !important;
    height: 4px !important;
    background: rgba(142, 152, 168, 0.40);
    opacity: 1 !important;
    border-radius: 6px !important;
    border: rgba(106, 153, 165, 1) !important;
    transform: none !important;
}


/* BREAKPOINT - NOTEBOOK  */
@media (max-width:1480px) and (min-width:998px){
    .features-3 .title-section {
        width: 100% !important;
    }

}


    .features-row > div {
        width: 20% !important;
    }

/* BREAKPOINT - TABLET  */
@media (max-width:990px) and (min-width:768px) {
    .hero-slider-left .title,
    .features-3 .title-section,
    .about-2-cards .title,
    .wrapper-testimonials-4tech .title-section {
        font-size: 38px !important;

    }
    .hero-slider-left .main-content {
        min-height: 229px !important;
    }
    .hero-slider-left .swiper-pagination{
        bottom: 15px !important;
        margin-top: 10px;
    }
    .swiper-pagination-bullet{
        margin-top: 15px;
        margin-bottom: 15px;
    }


    .wrapper-testimonials-4tech .text-1{
        width: 34% !important;
        position: relative;
    }
}

/* BREAKPOINT - CELULAR  */
@media (max-width: 768px) and (min-width:320px) {
    .hero-slider-left .buttons .button-cta{
        min-width: 343px;
    }

    .hero-slider-left .buttons {
        flex-direction: column;
        gap: 14px;
    }

    .hero-techmax{
        height:600px !important;
    }
     .testimonials-2 .display-4, .testimonials-2 .lead{
         text-align:initial !important;
     }

     .swiper-slide p, .info h5, .info h5{
         font-size:15px;
     } 

    .swiper-hero, .swiper-autoheight, .swiper-wrapper {
        height: 600px !important;

    }


    .testimonials-2 .swiper-wrapper .swiper-slide{
            margin-right: 35px !important;
    }

    .wrapper-testimonials-4tech .title-section,
    .about-2-cards .title,
    .hero-slider-left .title,
    .features-3 .title-section, .testimonials-2 .display-4{
        font-size: 32px !important;
        line-height: 40px !important;
    }
    .features-3 .title-section,
    .features-3 .subtitle-section,
    .wrapper-testimonials-4tech .title-section {
        text-align: left !important;
    }
    .hero-slider-left .swiper-pagination-bullets{
        bottom: 8px !important;
    }
    .about-2-cards .text-second {
        text-align: justify !important;
    }
    .about-2-cards .card-body .text-second {
        text-align: center !important;
    }

    .swiper-pagination-bullet{
        margin-top: 15px;
        margin-bottom: 15px;
    }

    .wrapper-testimonials-4tech .swiper-backface-hidden{
        position: relative;
        bottom: 63px;
    }
    .wrapper-testimonials-4tech .text{
        width: 80% !important;
        position: relative;
        left: -10px;
    }

    .wrapper-testimonials-4tech .info .title-testimonials{
        position: absolute;
        left: 12%;
    }
    .wrapper-testimonials-4tech .info .text-second{
        position: absolute;
        left: 15%;
    }

    .features-3 .title-section, .faq-1 .title{
        font-size: 32px !important;
    }

    .faq-1 .accordion-collapse p{
        padding: 0px 24px;
    }
    .wrapper-testimonials-4tech .text{
        font-size: 15px !important;
    }
}

