$(document).ready(function() {
    $('section[data-section-idx="4"] .container').removeClass('container').addClass('container-fluid');
});

function share() {
    tituloObrigado = document.querySelector('.title.in').innerText
    console.log(tituloObrigado)
    if (navigator.share !== undefined) {
        navigator.share({
            title: tituloObrigado + ' - Amigos',
            text: tituloObrigado,
            url: window.location.pathname,
        })
        .then(function() {
            console.log('Sucesso');
        })
        .catch(function(error) {
            console.log('Erro', error);
        });
    }
}

