context_script:
customer = frappe.get_list("Customer", limit_page_length=1)

if customer:
    customer = customer[0]

context.customer = customer.name

context.plans = [
    frappe.get_doc("Subscription Plan", plan)
    for plan in frappe.db.get_list("Subscription Plan", filters={'is_addon': 0})
]

