    color: rgba(79, 96, 125, 1) !important;
    font-size: 16px;
    margin: 0;
}
body {
    font-family: 'Urbanist', sans-serif;
}
header {
    border-bottom: 1px solid #ECEFF3;
    padding: 20px 50px;
    align-items: center;
    font-weight: 700;
}
p {
    margin-bottom: 0 !important;
}
nav, footer {
    display: none !important;
}
