    padding: 33px 50px;
}
.title-plans {
    font-weight: 700;
    color: #4F607D;
    font-size: 18px;
    line-height: 120%;
}
.subtitle-plans {
    font-weight: 500;
    font-size: 16px;
    line-height: 140%;
    color: #4F607D;
    margin-top: 7px;
    margin-bottom: 26px;
}
/* plan cards */

.cards-grid {
    display: grid;
    gap: 20px;
    grid-template-columns: repeat(auto-fit, minmax(254px, 1fr));
}

.card-plan {
    padding: 16px;
    border-radius: 8px;
    display: flex;
    flex-direction: column;
    background: #FFF;
    border: 1px solid #ECEFF3;
}

.card-plan-content {
    display: flex;
    flex-direction: column;
    height: 100%;
    justify-content: space-between;
    padding: 0;
    gap: 23px;
}
.card-content-top {
    display: flex;
    flex-direction: column;
    padding: 0;
    gap: 4px;
}
.card-top-header {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
}
.card-header-titles {
    display: flex;
