javascript:
$('.subscribe').on('click', ev => {
    let el = $(ev.target),
        customer = el.data('customer'),
        plan = el.data('plan');

    frappe.call({
        'method': 'techmax_solucoes.api.subscribe',
        'type': 'post',
        'btn': el,
        'message': __('Lauching your Subscription'),
        'args': {
            customer,
            plan
        }
    }).then(res => {
        window.location = res.message.payment_test
    });

});

