javascript:
let warn = document.querySelector('.warn');

let inputs = document.querySelectorAll('input');

inputs.forEach(input => {
    if (input.classList.contains('mandatory')) {
        input.addEventListener('blur', (event) => {
            if (event.currentTarget.value.length === 0) {
                event.currentTarget.classList.add('invalid-input');
            } else {
                event.currentTarget.classList.remove('invalid-input');
            }
        })
    }
})

function toggleInput() {
    let input = document.querySelector('#promo-input');
    let button = document.querySelector('#promo-btn');

    [input, button].forEach(i => i.classList.toggle('hide'));
}

