context_script:
year = frappe.utils.today().split('-')[0]
year_start = f'{year}-01-01'
year_end = f'{year}-12-31'
context.year_start = year_start
context.year_end = year_end
ecommerce_settings = frappe.get_doc("E Commerce Settings", "E Commerce Settings")
customer = frappe.get_list("Customer", limit_page_length=1)

if customer:
    customer = customer[0]

    statements = frappe.call("frappe.desk.query_report.run", report_name="General Ledger", filters={
        "company": ecommerce_settings.company,
        "from_date": year_start,
        "to_date": year_end,
        "party_type": "Customer",
        "party": [customer.name],
        "group_by": "Group by Voucher (Consolidated)",
        "include_dimensions": True
    })
    context.statement = statements

    links = frappe.get_all('Dynamic Link', fields=['parent'], filters={
        'link_doctype': 'Address',
        'link_name': customer.name
    })

    addresses = frappe.get_all('Address', fields='*', filters={
        'name': ['in', [l.parent for l in links]],
        'is_primary_address': True
    })

    context.links = links
    context.addresses = addresses
    context.customer = frappe.get_doc("Customer", customer.name)
    context.address = addresses[0] if addresses else {}

else:
    context.statement = []
    context.customer = {}
    context.address = {}

