context_script:
projects = []

for project in frappe.get_list('Project', fields=['name', 'status'], order_by="creation asc"):
    statuses = {}
    for task in frappe.get_list('Task', fields='*', filters={'project': project.name}):
        status = task["status"]
        if status in statuses:
            statuses[status].append(task)
        else:
            statuses[status] = [task]

    project.task = statuses
    projects.append(project)

    qtd = 0
    for status, lista in project.task.items():
        qtd = qtd + len(lista)
    project.total_tasks = qtd

context.projects = projects

