const tabs = document.querySelectorAll('#myTab')
const links = document.querySelectorAll('button.nav-link')

links.forEach((link, index) => {

    link.addEventListener('click', (e)=> {
        links.forEach(item => {
            document.querySelector(item.dataset.bsTarget).classList.remove('active')
            item.classList.remove('active')
        })
        link.classList.add('active')
        document.querySelector(link.dataset.bsTarget).classList.add('active')
        document.querySelector(link.dataset.bsTarget).classList.add('show')

        let line = document.querySelector('.line-bottom')
        line.style.width = e.currentTarget.offsetWidth + 'px'
        line.style.left = e.currentTarget.offsetLeft + 'px'
    })
})

