javascript:
let record = {};

$('.input, .options').on('change', ev => {
    let el = $(event.target);
    record[el.attr('name')] = el.val();
    console.log(record);
});


$('.schedule-now').on('click', ev => {
    ev.preventDefault();

    if (!Object.keys(record).length === 5) {
        frappe.throw('Todos os campos são obrigatórios');
    }

    frappe.call({
        'method': 'frappe.website.doctype.web_form.web_form.accept',
        'args': {
            'data': record,
            'for_payment': false,
            'web_form': 'landing-page-contato',

        },
        'callback': res => {
            frappe.msgprint('Obrigado pelo seu contato, retornaremos em breve!');
        }
    });
})

$('.footer-webinar .container').addClass('container-fluid')
$('.container-fluid').removeClass('container')
