$(document).ready(function () {
  var index = 1;

  function alterarTexto() {
    $('#textoAnimado' + index).removeClass('active');
    index = (index % 3) + 1;
    $('#textoAnimado' + index).addClass('active');
  }
  alterarTexto();

  setInterval(alterarTexto, 3000);
});
