custom_overrides:
@import url('https://fonts.googleapis.com/css2?family=Manrope:wght@400;500;600&family=Public+Sans:wght@300;400;500;600;700&family=Work+Sans:wght@400;500;600&display=swap');

$primary: #216778;
$second: rgba(33, 103, 120, 0.08);
$title-text : #22282F;
$text-primary: #38424E;
$text-second: #656D78;
$text-third: #343F52;
$text-fourth: #60697B;
$element-primary: #FFF;
$element-second: #72B6C5;
$element-third: #E2626B;
$element-fourth: #45C4A0;
$element-fifth: #F78B77;
$element-sixth: #605DBA;


$theme-colors: (
  "primary": $primary,
  "second": $second,
  "title-text": $title-text,
  "text-primary": $text-primary, 
  "text-second": $text-second,
  "text-third": $text-third,
  "text-fourth": $text-fourth,
  "element-primary": $element-primary,
  "element-second": $element-second,
  "element-third": $element-third,
  "element-fourth": $element-fourth,
  "element-fifth": $element-fifth,
  "element-sixth": $element-sixth
);

::selection {
    background: $primary !important;
    color: white !important;
}

.offcanvas-backdrop {
    width: 100% !important;
    height: 100% !important;
}

.title{
	color: $title-text !important;
	font-family: Work Sans;
	font-size: 48px !important;
	font-style: normal;
	font-weight: 600 !important;
	line-height: 46px;
	letter-spacing: -0.5px;
}
.text{
	color: $text-primary;
	text-align: center;
	font-family: Work Sans;
	font-size: 22px !important;
	font-style: normal;
	font-weight: 400 !important;
	line-height: 30px; 
	letter-spacing: -0.5px;
}
.card-text{
	color: $text-primary;
	text-align: center;
	font-family: Work Sans;
	font-size: 22px !important;
	font-style: normal;
	font-weight: 600 !important;
	line-height: 30px; 
	letter-spacing: -0.5px;
}
.text-primary{
	color: $text-second !important;
	text-align: center !important;
	font-family: Work Sans;
	font-size: 18px !important;
	font-style: normal;
	font-weight: 500 !important;
	line-height: 24px;
}
.text-second{
	color: $text-primary;
	font-family: Manrope;
	font-size: 15px !important;
	font-style: normal;
	font-weight: 500 !important;
	line-height: 22px;
	letter-spacing: 0.43px;
}
.text-footer{
	color: #E9F0F2;
	font-family: Manrope;
	font-size: 15px !important;
	font-style: normal;
	font-weight: 600 !important;
	line-height: 22px;
	letter-spacing: 0.43px;
}
.title-card{
	color: $text-third !important;
	font-family: Work Sans;
	font-size: 18px !important;
	font-style: normal;
	font-weight: 600 !important;
	line-height: 24px;
}
.title-testimonials{
	color: $title-text  !important;
	font-family: Work Sans;
	font-size: 18px !important;
	font-style: normal;
	font-weight: 600 !important;
	line-height: 24px;
}
.text-card{
	color: $text-fourth !important;
	font-family: Manrope;
	font-size: 15px !important;
	font-style: normal;
	font-weight: 500 !important;
	line-height: 22px; 
	letter-spacing: 0.43px;
	padding-left: 0px !important;
}
.text-navbar{
    color: $text-primary !important;
    font-family: Manrope;
    font-size: 13px !important;
    font-style: normal;
    font-weight: 500 !important;
    line-height: 20px;
    margin-bottom: 0 !important;
}
.title-nav{
    color:  $title-text !important;
    font-family: 'Work Sans';
    font-size: 14px !important;
    font-style: normal;
    padding-left: 19px;
    font-weight: 500 !important;
    line-height: 24px;
    text-transform: uppercase;
}
.text-button{
    font-family: 'Public Sans' !important;
    font-size: 17px !important;
    font-style: normal !important;
    font-weight: 500 !important;
    line-height: 22px !important;
    letter-spacing: 0.43px !important;
}

/*Navbar*/
.navbar {
    padding: 12px 26px !important;

}

.buttons-select::marker{
    display: none !important;
}
.sobre-grid{
    display: grid !important;
    grid-template-columns: 1fr 1fr !important;
    gap: 10px;
}
.dropdown-menu-techmax{
    display: flex;
    width: 634px;
    max-width: 634px;
    padding: 24px !important;
}
.dropdown-menu-sobre{
    flex-direction: column;
    align-items: flex-start;
    gap: 10px;
}
.dropdown-menu-idioma{
