    background: rgba(208, 229, 233, 0.30);
}
.dropdown .dropdown-menu-techmax .nav-item.esp:hover {
    background: rgba(181, 231, 217, 0.30);
}
.dropdown .dropdown-menu-techmax .nav-item.equipe:hover {
    background: rgba(217, 203, 232, 0.30);
}
.dropdown .dropdown-menu-sobre .nav-item.cloud:hover {
