}
.dropdown .dropdown-menu-sobre .nav-item.tic:hover {
    background: rgba(252, 209, 201, 0.30);
}
.dropdown .dropdown-menu-sobre .nav-item.ip:hover {
    background: rgba(181, 231, 217, 0.30);
}
.dropdown .dropdown-menu-sobre .nav-item.dev:hover {
