}
.dropdown-item:hover, .dropdown-item:focus{
    background-color: transparent !important;
}

.button-primary {
    background-color: #216778 !important;
    border-radius: 8px;
    padding: 13px 26px !important;
    color: white !important;
    text-align: center !important;
}

